<div class="container">  <video controls playsinline autoplay preload="metadata" <source type="application/x-mpegURL" src="https://dl.NoGutsNoGlory.eu.org/761948/Salaar+Part+1+%282023%29+Hindi.mkv?hash=ZRfXTkN">  </video>  </video></div><!-- Plyr resources and browser polyfills are specified in the pen settings --><script src="https://cdn.rawgit.com/video-dev/hls.js/18bb552/dist/hls.min.js"></script><script src="https://unpkg.com/plyr@3"></script><link rel="stylesheet" href="https://cdn.jsdelivr.net/gh/CDNSFree2/Plyr/plyr.css" /><style>  .container {    width: 100%;    hight: auto;  }  video {    aspect-ratio: 16 / 9;    width: 100%;    hight: auto;  }</style><script>  // hls player  document.addEventListener('DOMContentLoaded', () => {    const video = document.querySelector('video');    const source = video.getElementsByTagName("source")[0].src;    const player = new Plyr(video);    const defaultOptions = {};    if (!Hls.isSupported()) {      video.src = source;    } else {      const hls = new Hls();      hls.loadSource(source);      hls.attachMedia(video);      window.hls = hls;    }    // Expose player so it can be used from the console    window.player = player;  });  // custimizing controls  const player = new Plyr('video', {    controls: ['play-large','progress', 'current-time','duration','play','fullscreen']  });  player.config.urls.download = 'https://asianhdplay.net/download?id=MzY5OTc4&title=Running+Man+%282010%29&typesub=SUB';</script>